import './admin.css'
import {useState} from 'react'
import {lo} from 'firebase/auth'
export default function Admin() {
    const [task, setTask] = useState('')
    function handleRegistrer(e) {
     e.preventDetault()
    }
    return(
        <div className='admin-container'>
            <h1>Minhas Tarefas</h1>

          <form onSubmit={handleRegistrer} className='form'>
            <textarea placeholder='Digite sua Tarefa'
            value={task}
            onChange={(e)=> setTask(e.target.value)} 
            />
            <button type='submit' className='btn-register'>Registrar Tarefa</button>
          </form>  

          <article className='list'>
            <p>Estudar + React</p>
            <div>
                <button className='btn-edit'>Editar</button>
                <button className='btn-checked'>Concluir</button>
            </div>
          </article>
          <button className='btn-logout' onClick={handleLogOut}>Sair</button>
        </div>
    )
}
