import {useState} from 'react'
import './index.css';
import { db, auth } from './firebaseConnection'
import {createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut} from 'firebase/auth'
import { async } from '@firebase/util';


function App() {
  const [email,setEmail] = useState('')
  const [senha,setSenha] = useState('')
  const [user, setUser] = useState(false)
  const [userDetails, setUserDetails] = useState({})

  async function newUser () {
    await createUserWithEmailAndPassword(auth, email, senha)
    .then(()=> {
      console.log("Cadastro Realizado com Sucesso")
      setEmail('')
      setSenha('')
    })
    .catch((error)=>{
      if(error.code === 'auth/email-already-in-use') {
        alert('EMAIL JÁ É USADO')
      } else if (error.code === 'auth/weak-password') {
        alert('SENHA MUITO FRACA')
      }
    })
  }

  async function loginUser () {
    await signInWithEmailAndPassword(auth, email, senha)
    .then((value)=> {
      setEmail('')
      setSenha('')
      setUser(true)
      setUserDetails({
        email: value.user.email
      })

    })
    .catch(()=>{
      alert('INSIRA SUAS INFORMAÇÕES CORRETAMENTE')
    })
  }

  return (
    <div>
     <h2>Cadastro de Usuários</h2>

     {user && (
      <div className='loginUser'>
          <h3>Seja Bem-Vindo ao sistema, você fez o login seu cu de osvaldo</h3>
          <strong>Email: {userDetails.email}</strong>
          <button onClick={logOut}>Sair da Conta</button>
      </div>
     )}

     <div className='container'>
     <strong>Email:</strong>
     <input type='text'
     value={email}
     onChange={(e)=> setEmail(e.target.value)}
     placeholder="Informe seu email"
     />

     <strong>Senha:</strong>
     <input type='text'
     value={senha}
     onChange={(e)=> setSenha(e.target.value)}
     placeholder="Informe sua Senha"
     />

     <button onClick={newUser}>Cadastrar</button>
     <button onClick={loginUser}>Fazer Login</button>
     </div>

    </div>
  );
}

export default App;
